import React, {useState} from "react";

const Form = () => {
    const [userInput, setUserInput] = useState({
        'title':'',
        'status':'',
        'creator':''
    })
    const titleHandler = (event)=>{
        // console.log(event.target.value)
        setUserInput((prevState) =>{
            return {...prevState, title:event.target.value}
        } )
    }

    const statusHandler = (event)=>{
        // console.log(event.target.value)
        setUserInput((prevState) =>{
            return {...prevState, status:event.target.value}
        } )
    }

    const creatorHandler = (event)=>{
        // console.log(event.target.value)
        setUserInput((prevState) =>{
            return {...prevState, creator:event.target.value}
        } )
    }

    return (
        <form id="formast">
          <div className="form-group"> <input className="form-control" type="text" placeholder="Pavadinimas" required onChange={titleHandler}/></div>
            <h6>Busena</h6>
          <div className="form-group">  <input className="form-control" list="buse" id="buseCho" placeholder="Busena" onChange={statusHandler} required/>
            <datalist id="buse">
                <option>Atvira</option>
                <option>Atlikta</option>
            </datalist>
          </div>
            <fieldset>
            <div className="form-group"><input className="form-control" type="text" placeholder="Autorius" required onChange={creatorHandler}/> </div>
            </fieldset>
         <button className="btn btn-primary">Saugoti</button>
        </form>
    )
}

export default Form;
